<div ng-if="loggedIn &amp;&amp; tasks" class="task-container"><div class="row"><div class="col-md-12"><h2>Task Manager</h2><button ng-click="addNew.isCollapsed = !addNew.isCollapsed" class="btn btn-primary add-new">add</button></div></div><div collapse="addNew.isCollapsed" class="row"><div class="col-md-4"></div><div class="col-md-4"><form name="addNewTask" method="post" class="add-new"><label for="assignTo">For: </label><select name="assignTo" add-user="add-user" class="form-control"><option>&mdash; assign somebody &mdash;</option><option ng-repeat="user in users">{{user.admin_username}} ({{user.admin_firstname}} {{user.admin_lastname}})</option></select><p ng-show="addNew.task.assignedTo"><span ng-repeat="user in addNew.task.assignedTo['users']"><a href="" remove-user="remove-user" tooltip="click to remove {{user}}" tooltip-placement="bottom">{{user}}</a><!--button.btn.btn-default.remove(type="button" value="{{user}}" remove-user) x-->&nbsp;&nbsp;</span></p><p><span ng-repeat="error in addNew.messages['errors'] track by $index">{{error}}</span></p><label for="content">Task:</label><textarea name="content" ng-model="addNew.task.content" placeholder="e.g., Make it squishy" class="form-control"></textarea><label for="dateDue">By When:</label><select name="dateDue" toggle-date="toggle-date" class="form-control"><option>&mdash; pick a date &mdash;</option><option>Sometime in the future</option><option>A day from now</option><option>Two days from now</option><option>Three days from now</option><option>A week from now</option></select><!--label(for="date") Date Due:--><!--datepicker.well.date(name="date" ng-model="addNew.task.dateDue" min-date="minDate" show-weeks="true")--><div class="btn-group"><button ng-click="setTask()" class="btn btn-primary">OK</button></div></form></div><div class="col-md-4"></div></div><div class="row"><div class="col-md-1"></div><div class="col-md-10"><table class="table table-condensed table-hover"><thead><tr><th>Task</th><th>For</th><th>By</th><th>When</th><th></th></tr></thead><tbody ng-repeat="task in tasks"><tr><td>{{task.content}}</td><td><span ng-repeat="user in task.assignedTo['users']">{{user}}<br/></span></td><td>{{task.assignedBy}} on {{task.dateAssigned}}</td><td>{{task.dateDue}}</td><td></td></tr></tbody></table></div><div class="col-md-1"></div></div></div>