<div ng-if="loggedIn &amp;&amp; tasks" class="task-container"><div class="row"><div class="col-md-12"><h2>Task Manager</h2><button ng-click="addNew.isCollapsed = !addNew.isCollapsed" class="btn btn-primary add-new">add</button></div></div><div collapse="!addNew.isCollapsed" class="row"><div class="col-md-4"></div><div class="col-md-4"><form name="addNew" method="post" class="add-new"><label for="assignTo">For: </label><select name="assignTo" ng-model="assignedTo" class="form-control"><option ng-repeat="user in users">{{user.admin_username}} ({{user.admin_firstname}} {{user.admin_lastname}})</option></select><label for="content">Task:</label><textarea name="content" ng-model="taskContent" placeholder="e.g., Make it squishy" class="form-control"></textarea><label for="date">Date Due:</label><datepicker name="date" ng-model="dateDue" min-date="minDate" show-weeks="true" class="well date"></datepicker><div class="btn-group"><button ng-submit="setTask()" class="btn btn-primary">OK</button></div></form></div><div class="col-md-4"></div></div><div class="row"><div class="col-md-1"></div><div class="col-md-10"><table class="table table-condensed table-hover"><thead><tr><th>Task</th><th>For</th><th>By</th><th>When</th><th></th></tr></thead><tbody ng-repeat="task in tasks"><tr><td>{{task.content}}</td><td><span ng-repeat="user in task.assignedTo">{{user}}<br/></span></td><td>{{task.assignedBy}} on {{task.dateAssigned}}</td><td>{{task.dateDue}}</td><td></td></tr></tbody></table></div><div class="col-md-1"></div></div></div>